        jLabel11.setFont(new java.awt.Font("Tahoma", 0, 13)); // NOI18N
        jLabel11.setText("• LPG can cook faster than any other fuels because of its high calorific value.");

        jLabel12.setFont(new java.awt.Font("Tahoma", 0, 13)); // NOI18N
